import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import {Link, useHistory} from "react-router-dom";
import { getTypesDiets, postTypeDiets } from "../actions";


export default function RecipeCreate(){
    const dispatch = useDispatch();
    const typediets = useSelector((state)=>state.typesDiets)

    const [input, setInput] = useState({
        name: "",
        dishSummary: "",
        points: "",
        healthyLevel: "",
        stepByStep: "",
        typeDiets: []
    })

    function handleChange(e){
        setInput({
            ...input,
            [e.target.name] : e.target.value
        })
    }

    useEffect(()=>{
        dispatch(getTypesDiets());
    },[]);

    return(
        <div>
            <Link to='/home'><button>Back</button></Link>
            <h1>Create Your Recipe!</h1>
            <form>
                <div>
                    <label>Name:</label>
                    <input type='text' value={input.name} name='name'/>
                </div>
                <div>
                    <label>Dish Summary:</label>
                    <input type='text' value={input.dishSummary} name='dish summary'/>
                </div>
                <div>
                    <label>Points:</label>
                    <input type='text' value={input.points} name='points'/>
                </div>
                <div>
                    <label>Healthy Level:</label>
                    <input type='text' value={input.healthyLevel} name='healthyLevel'/>
                </div>
                <div>
                    <label>Step By Step:</label>
                    <input type='text' value={input.stepByStep} name='stepByStep'/>
                </div>
                <select>
                    {typediets.map(type=>{
                        <option value={type.name}></option>
                    })}
                </select>

                <button type='submit'>Create Recipe!</button>
            </form>
        </div>
    )
}
