import React, { Fragment } from 'react' ; 
import {useState, useEffect} from 'react';
import {useDispatch, useSelector} from 'react-redux';
import getRecipes from '../actions/index.js';
import {Link} from 'react-router-dom';
import Card from './Card';
//const allRecipes =[{name:'hola', id:23423},{name:'chau', id:2421}]
export default function Home(){
    const dispatch = useDispatch();
    const [currentPage, setCurrentPAge] =useState(1);
    const [RecipePerPage, setRecipePerPage] = useState(10);
    const indexOfLAstRecipe  = currentPage * RecipePerPage


    const allRecipes = useSelector((state)=> state.recipes);
    useEffect(()=>{
        dispatch(getRecipes())
    },[dispatch])
    console.log("Allrecipes:",allRecipes)
    // en select podriamos hacer un maps.
    return(

        <div>
            <Link to='/recipe'>Create Recipe</Link>
            <h1>RECIPES</h1>
            <div>
                <select>
                    <option value="asc">Ascendente</option>
                    <option value="desc">Descendente</option>
                </select>
                <select>
                    <option value="gluten free">Gluten Free</option>
                    <option value="dairy free">Dairy Free</option>
                    <option value="lacto ovo vegetarian">lacto ovo vegetarian</option>
                    <option value="vegan">Vegan</option>
                    <option value="paleolithic">paleolithic</option>
                    <option value="primal">Primal</option>
                    <option value="pescatarian">Pescatarian</option>
                    <option value="fodmap friendly">fodmap friendly</option>
                    <option value="whole 30">Whole 30</option>
                </select>
                {
                    allRecipes?.map((c)=>{
                        return(
                            <fragment>
                                <Link to={'/home' + c.id}>
                                    <Card name={c.name} image={c.img} type={c.type} />
                                </Link>
                            </fragment>
                        )
                    })
                }
            </div>
        </div>
    )   
}
