import React from 'react';
import{ useState } from 'react';
import { orderByName } from '../actions/index';
import { useDispatch } from 'react-redux';
import './Select.css';



export default function Select({funcion, arrValue, arrName}){

    return(
        <select className='select' onChange={funcion}>
            {
                arrValue?.map((value,indice)=>{
                    return(
                        <option value={value}>{arrName[indice]}</option>
                    )
                })
            }
        </select>
    )
}
