import axios from 'axios';


export function getRecipes(){
    return async function(dispatch){
        var json = await axios.get("http://localhost:5000/recipes")
        return dispatch({
            type: 'GET_RECIPES',
            payload : json.data
        }) 
    }
}

export function filterRecipesByTypes(payload){
    console.log(payload)
    return{
        type:'FILTER_BY_VALUE',
        payload
    }
}
