import React from 'react';
import{useState} from 'react';
import {orderByName} from '../actions/index';

const [ordenName, setOrdenName] = useState(''); 


export default function Select({funcion, arrValue, arrName}){
    const dispatch = useDispatch();

    function handleSortName(e){
        dispatch(orderByName(e.target.value))
        setCurrentPage(1);
        setOrdenName(`Ordenado ${e.target.value}`)
    }

    return(
        <select onChange={funcion}>
            {
                arrValue?.map((value,indice)=>{
                    return(
                        <option value={value}>{arrName[indice]}</option>
                    )
                })
            }
        </select>
    )
}
