import React, { Fragment } from 'react' ; 
import {useState, useEffect} from 'react';
import {useDispatch, useSelector} from 'react-redux';
import getRecipes from '../actions/index.js';
import {Link} from 'react-router-dom';
import Card from './Card';
//const allRecipes =[{name:'hola', id:23423},{name:'chau', id:2421}]
export default function Home(){
    const dispatch = useDispatch();
    const allRecipes = useSelector((state)=> state.recipes);
    useEffect(()=>{
        dispatch(getRecipes())
    },[dispatch])
    console.log("Allrecipes:",allRecipes)
    // en select podriamos hacer un maps.
    return(

        <div>
            <Link to='/recipe'>Create Recipe</Link>
            <h1>RECIPES</h1>
            <div>
                <select>
                    <option value="asc">Ascendente</option>
                    <option value="desc">Descendente</option>
                </select>
                <select>
                    <option value="gluten free">Gluten Free</option>
                    <option value="dairi free">Dairi Free</option>
                    <option value="lacto ovo vegetarian">Lacto Ovo Vegetarian</option>
                    <option value="vegan">Vegan</option>
                    <option value="paleolithic">Paleolithic</option>
                    <option value="primal">Primal</option>
                    <option value="pescatarian">Pescatarian</option>
                    <option value="todmap friendly">Todmap Friendly</option>
                    <option value="whole 30">Whole 30</option>
                </select>
                {
                    allRecipes?.map((c)=>{
                        return(
                            <fragment>
                                <Link to={'/home' + c.id}>
                                    <Card name={c.name} image={c.img} />
                                </Link>
                            </fragment>
                        )
                    })
                }
            </div>
        </div>
    )   
}
